import { useEffect, useState } from "react";
import { Button, Card, CardBody,  CardHeader,  CardSubtitle, CardText, CardTitle, Nav  } from "react-bootstrap";
 import SidebarComponent from "./sidebar";
import axios from "axios";
import { useSearchParams } from "react-router-dom";

function HomeComponent(props){
    const [param] = useSearchParams();
  const [featuredBooks,setFeaturedBooks] = useState([]);
  const [msg,setMsg] = useState('');
  const [searchBooks,setSearchBooks] = useState([])

  useEffect(()=>{
     if(props.strVal !== ''){
       axios.get('http://localhost:8182/Book/all'+props.strVal )
     .then(response=> setSearchBooks(response.data))
     }
     else{
       setSearchBooks([])
     }
     axios.get('http://localhost:8182/customer/getbook/'+param.get('bookTitle'))
     .then(response=> setFeaturedBooks(response.data))
     .catch(error=> setMsg('Error in Fetching Books'));

 
 },[props.strVal])
 
 return (
   <div className="container-fluid">
     <div className="row">
       <div className="col-md-3">
         <SidebarComponent />
         
       </div>
       <div className="col-md-9">
       <div className="row">
        

           {searchBooks.map((p, index) => (
             <div key={index} className="col-md-4 mb-4">
               <Card
                 style={{
                   width: "90%",
                   height: "100%",
                 }}
               >
                 <CardHeader>Search Results</CardHeader>
                 {/* <img alt="Sample" src="https://picsum.photos/300/200" /> */}
                 <CardBody>
                   <CardTitle tag="h5">{p.title}</CardTitle>
                   <CardSubtitle className="mb-2 text-muted" tag="h6">
                     Price: INR. {p.price}
                   </CardSubtitle>
                   <CardText>{p.description}</CardText>
                   <Button>Button</Button>
                 </CardBody>
               </Card>
               <Nav.Link> </Nav.Link>
             </div>
           ))}
         </div>

         <div className="row">
         {searchBooks.length !== 0?<div>
           <hr />
         </div>:''}
           {featuredBooks.map((p, index) => (
             <div key={index} className="col-md-4 mb-4">
               <Card
                 style={{
                   width: "90%",
                   height: "100%",
                 }}
               >
                 <CardHeader>Featured</CardHeader>
                 {/* <img alt="Sample" src="https://picsum.photos/300/200" /> */}
                 <CardBody>
                   <CardTitle tag="h5">{p.title}</CardTitle>
                   <CardSubtitle className="mb-2 text-muted" tag="h6">
                     Price: INR. {p.price}
                   </CardSubtitle>
                   <CardText>{p.description}</CardText>
                   <Button>Button</Button>
                 </CardBody>
               </Card>
               <Nav.Link> </Nav.Link>
             </div>
           ))}
         </div>
       </div>
     </div>
   </div>
 );
}
export default HomeComponent;
