import { useEffect, useState } from "react";

import Login from "../Auth/login/login";
import Books from "./book";

import { useSearchParams } from "react-router-dom";
import NavbarComponent from "../navbar";
import axios from "axios";

import { Card, CardHeader, ListGroup, ListGroupItem, Nav, Row } from "react-bootstrap";

function CustomerDashboard() {
    const [param] = useSearchParams();
    const [qStr, setQstr] = useState('');
    const[book,setBooks]=useState([ ]); 
    const[book1,setBooks1]=useState([ ]); 
    const [categories, setCategories] = useState([]);
    const [msg, setMsg] = useState('');
    const [loading,setLoading]=useState(false);
    const [category,setCategory]=useState([]);

    useEffect(()=>{
        axios.get('http://localhost:8182/Book/all')
        .then(response=>setBooks(response.data))
    })
     
    axios.get('http://localhost:8182/customer/getallcategory')
    .then(response => {
      // Use Set to remove duplicates
      const uniqueCategories = [...new Set(response.data.map(c => c.category))];
      setCategories(uniqueCategories.map(category => response.data.find(c => c.category === category)));
    })
    .catch(error => setMsg('Error in Fetching categories'));

    const searchBooks = (str) => {
        console.log('search func in parent comp called.....' + str)
        setQstr(str);
    };

    const headingStyle = {
        fontFamily: 'Verdana, sans-serif',
        fontSize: '28px',
        color: '#4CAF50', // Green color
        fontWeight: 'bold',
        margin: '10px 0',
    };


    const getbookcategory = async () => {
    try {
        console.log(category);
        setLoading(true);
        const response = await axios.get(`http://localhost:8182/customer/getbycategory?category=${category}`);
        console.log('API response:', response);
        setBooks1(response.data || []);
    } catch (error) {
        console.error('Error searching books:', error);
    } finally {
        setLoading(false);
    }
}


    // const process = () => {
    //     if (!param.get('page')) {
    //         return (
    //             <div>
    //                 <h2 style={headingStyle}>WELCOME TO INFOLIBRA</h2>
    //                 <HomeComponent strVal={qStr} />
    //             </div>
    //         );
    //     }
    //     if (param.get('page') === 'books') {
    //         return (
    //             <div>
    //                 <h2 style={headingStyle}>Books Page</h2>
    //                 <Books />
    //             </div>
    //         );
    //     }
    //     if (param.get('page') === 'cart') {
    //         return (
    //             <div>
    //                 <h1 style={{ color: '#FF5733' }}>Cart Page</h1>
    //             </div>
    //         );
    //     }
    //     if (param.get('page') === 'previous_orders') {
    //         if (localStorage.getItem('isLoggedIn') === null) {
    //             localStorage.setItem('url', '/customer/dashboard?page=previous_orders');
    //             return (
    //                 <div>
    //                     <h2 style={headingStyle}>Login Required</h2>
    //                     <Login />
    //                 </div>
    //             );
    //         }
    //         return (
    //             <div>
    //                 <h2 style={headingStyle}>Previous Orders Page</h2>
    //                 <h1 style={{ color: '#3366CC' }}>Previous Orders Content</h1>
    //             </div>
    //         );
    //     }
    // };

    return (
        <div >
            <NavbarComponent func={searchBooks} />
            {/* {process()} */}
            <Row style={{padding:20}}>
            <div className="col-md-4">
            <Card
        style={{
          width: "18rem",
        }}
      >
        <CardHeader>Categories</CardHeader>
        <ListGroup>
          {categories.map((c, index) => (
            <div key={index}>
              <ListGroupItem>
              <Nav.Link onClick={() => { getbookcategory(c.category); getbookcategory(); }}>{c.category}</Nav.Link>

                {/* <Nav.Link onClick={() => { setCategory();getbookcategory() }}>{c.category}</Nav.Link> */}
              </ListGroupItem>
            </div>
          ))}
        </ListGroup>
      </Card>
            </div>
            <div className="col-md-7">{book.map((b, index) => (
                <div key={index} className="col-md-4 mb-4">
                    <p>{b.bookTitle}</p>
                   </div>))}
                  
                    </div></Row>
        </div>
    );
}

export default CustomerDashboard;
