import React, { useEffect, useState } from 'react';
import { Button, Card, CardBody, CardHeader, CardSubtitle, CardText, CardTitle, Nav } from 'react-bootstrap';
import SidebarComponent from './sidebar';
import axios from 'axios';
import { useSearchParams } from 'react-router-dom';

function HomeComponent(props) {
  const [param] = useSearchParams();
  const [featuredBooks, setFeaturedBooks] = useState([]);
  const [msg, setMsg] = useState('');
  const [searchBooks, setSearchBooks] = useState([]);

  useEffect(() => {
    if (props.strVal !== '') {
      axios.get('http://localhost:8182/Book/all' + props.strVal)
        .then(response => setSearchBooks(response.data))
    } else {
      setSearchBooks([]);
    }
    axios.get('http://localhost:8182/customer/getbook/' + param.get('bookTitle'))
      .then(response => setFeaturedBooks(response.data))
      .catch(error => setMsg('Error in Fetching Books'));
  }, [props.strVal]);

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-3">
          <SidebarComponent />
        </div>
        <div className="col-md-9">
          {searchBooks.map((p, index) => (
            <div key={index} className="col-md-4 mb-4">
              <Card
                style={{
                  width: "90%",
                  height: "100%",
                  backgroundColor: "#f5f5f5", // Light gray background color
                  boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
                }}
              >
                <CardHeader style={{ backgroundColor: "#3498db", color: "#fff" }}>Search Results</CardHeader>
                <CardBody>
                  <CardTitle tag="h5" style={{ color: "#333", fontFamily: "Arial, sans-serif" }}>{p.title}</CardTitle>
                  <CardSubtitle className="mb-2 text-muted" tag="h6" style={{ color: "#777" }}>
                    Price: INR. {p.price}
                  </CardSubtitle>
                  <CardText style={{ color: "#555" }}>{p.description}</CardText>
                  <Button variant="primary">Button</Button>
                </CardBody>
              </Card>
              <Nav.Link> </Nav.Link>
            </div>
          ))}
          {/* Your existing code for displaying featured books */}
        </div>
      </div>
    </div>
  );
}

export default HomeComponent;
